import { db } from './firebaseConect';
import { useState } from 'react';
import { addDoc, collection, getDocs } from 'firebase/firestore';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './app.css';



function App() {

  // armazena dados dos inputs
  const [titulo, setTitulo] = useState();
  const [autor, setAutor] = useState();
  const [posts, setPosts] = useState();

  // grava dados dentro do db - collection
  async function handleAdd() {

    //setDoc, quando passa os paramentros manualmente

    /* await setDoc(doc(db, "posts", "123456"), {
       titulo: titulo,
       autor: autor
     })
       .then(() => {
         console.log('Cadastrado com sucesso');
         setTitulo('');
         setAutor('');
         toast.success('Cadastrado com sucesso!');

       })
       .catch((error) => {
         console.log('gerou erro' + error);

         toast.warn('Error! Algum problema no cadastro.')
       })*/

    if (titulo === '' || autor === '') {

      toast.warn('Preencha seus dados');
      return;

    } else {

      const addPost = collection(db, 'posts');
      await addDoc(addPost, {
        titulo: titulo,
        autor: autor
      })
        .then(() => {
          toast.success('Cadastrado com sucesso!');
          setTitulo('');
          setAutor('');
        })
        .catch((error) => {
          alert(error);
        })

    }

  }

  //consulta db - collection
  async function pesquisarPosts() {

    const postsRef = collection(db, "posts")
    await getDocs(postsRef)
    .then((snapshot) => {
      let lista = [];

      snapshot.forEach((doc) => {
        lista.push({
          id: doc.id,
          titulo: doc.data().titulo,
          autor: doc.data().autor,
        })
      })

      setPosts(lista);

    })
    .catch((error) => {
      console.log("DEU ALGUM ERRO AO BUSCAR")
    })
  }

  return (
    <div>
      <ToastContainer
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
      <div className='container'>
        <h1>Cadastro no firestore - reactjs + Firebase ;)</h1>

        <label>Titulo</label>
        <textarea type="text"
          placeholder='Digite seu titulo'
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
        />

        <label>Autor</label>
        <textarea type="text"
          placeholder='Autor do post'
          value={autor}
          onChange={(e) => setAutor(e.target.value)}
        />

        <button onClick={handleAdd}>Cadastrar</button>
        <button onClick={pesquisarPosts}>Buscar postagens</button>
      </div>
      <div>
        <h3>Resultados</h3>
        <ul>
          {posts.map((postagens) => {
            return(
              <li key={postagens.id}>
                <span>{postagens.titulo}</span>
                <span>{postagens.autor}</span>
              </li>
            )
          })}
        </ul>
      </div>
    </div>
  );
}

export default App;
