import { db } from './firebaseConect';
import { useState, useRef } from 'react';
import { addDoc, collection, doc, getDocs, updateDoc } from 'firebase/firestore';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './app.css';
import { async } from '@firebase/util';



function App() {

  const foco = useRef(null);
  // cad. aluno
  const [idAluno, setIdAluno] = useState();
  const [nomeAluno, setNomeAluno] = useState();
  const [sobreNomeAluno, setSobreNomeAluno] = useState();
  const [nomePai, setNomePai] = useState();
  const [contatoPai, setContatoPai] = useState();
  const [nomeMae, setNomeMae] = useState();
  const [contatoMae, setContatoMae] = useState();
  const [turno, setTurno] = useState();
  const [serie, setSerie] = useState();
  const [endereco, setEndereco] = useState();
  const [emailContato, setEmailContato] = useState();

  // armazena dados dos inputs
  const [idPosts, setIdPosts] = useState();
  const [titulo, setTitulo] = useState();
  const [autor, setAutor] = useState();
  //const [posts, setPosts] = useState();

  // grava dados dentro do db - collection
  async function handleAdd() {

    //setDoc, quando passa os paramentros manualmente

    /* await setDoc(doc(db, "posts", "123456"), {
       titulo: titulo,
       autor: autor
     })
       .then(() => {
         console.log('Cadastrado com sucesso');
         setTitulo('');
         setAutor('');
         toast.success('Cadastrado com sucesso!');

       })
       .catch((error) => {
         console.log('gerou erro' + error);

         toast.warn('Error! Algum problema no cadastro.')
       })*/

    if (titulo === '' || autor === '') {

      toast.warn('Preencha seus dados');
      return;

    } else {

      const addPost = collection(db, 'posts');
      await addDoc(addPost, {
        titulo: titulo,
        autor: autor
      })
        .then(() => {
          toast.success('Cadastrado com sucesso!');
          setTitulo('');
          setAutor('');
        })
        .catch((error) => {
          alert(error);
        })

    }

  }

  //consulta db - collection
  async function pesquisarPosts() {

    const pesquisar = collection(db, "posts")

    await getDocs(pesquisar)
      .then((snapshot) => {

        //Array - armazena dados db dentro do
        let lista = [];
        //percorre lista de dados dentro do db e armazena no array
        snapshot.forEach((doc) => {

          lista.push({
            id: doc.id,
            titulo: doc.data().titulo,
            autor: doc.data().autor
          })

          //   setPosts(lista);
        })

      })
      .catch((e) => {

      })
  }

  // atualizacao de collection do db
  async function editarPosts() {
    const docRef = doc(db, "posts", idPosts)
    await updateDoc(docRef, {
      titulo: titulo,
      autor: autor
    })
      .then(() => {
        toast.success('Atualizado com sucesso')
        setIdPosts('')
        setAutor('')
        setTitulo('')
      })
      .catch((error) => {
        toast.warn('error')
      })

  }

  //cadastrar aluno
  async function cadAluno(){
    if(nomeAluno === ''){
      toast.warn('Preencha o nome do aluno');
      useRef={foco}
    }
  }

  return (
    <div>
      <ToastContainer
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
      <div className='container'>
        <h1>TDC - Cadastro de alunos</h1>


        <label>Turno</label>
        <select value={turno} onChange={(e) => setTurno(e.target.value)}>
          <option value="Seeione a série">Turno</option>
          <option value="9 Ano">Manhã</option>
          <option value="8 Ano">Tarde</option>
        </select>

        <label>Série</label>
        <select value={serie} onChange={(e) => setSerie(e.target.value)}>
          <option value="Seeione a série">Selecione a série</option>
          <option value="9 Ano">9 Ano</option>
          <option value="8 Ano">8 Ano</option>
          <option value="7 Ano">7 Ano</option>
          <option value="6 Ano">6 Ano</option>
          <option value="5 Ano">5 Ano</option>
        </select>


        <label>Nome do Aluno</label>
        <input type="text"
          placeholder='Digite o nome do aluno'
          value={nomeAluno}
          onChange={(e) => setNomeAluno(e.target.value)}
          className='inputText'
          ref={foco}
        />

        <label>Sobrenome do Aluno</label>
        <input type="text"
          placeholder='Digite o sobrenome do aluno'
          value={sobreNomeAluno}
          onChange={(e) => setSobreNomeAluno(e.target.value)}
          className='inputText'
        />

        <label>Nome da Mãe</label>
        <input type="text"
          placeholder='Nome e Sobrenome da Mãe'
          value={nomeMae}
          onChange={(e) => setNomeMae(e.target.value)}
          className='inputText'
        />
        <label>Contato do Mãe</label>
        <input type="tel"
          mask="(99) 9 9999-9999"
          maxLength={11}
          placeholder='Contato do Pai'
          value={contatoMae}
          onChange={(e) => setContatoMae(e.target.value)}
          className='inputText'
        />
        <label>Nome do Pai</label>
        <input type="text"
          placeholder='Nome e Sobrenome do Pai'
          value={nomePai}
          onChange={(e) => setNomePai(e.target.value)}
          className='inputText'
        />
        <label>Contato do Pai</label>
        <input type="tel"
          mask="(99) 9 9999-9999"
          maxLength={11}
          placeholder='Contato do Pai'
          value={contatoPai}
          onChange={(e) => setContatoPai(e.target.value)}
          className='inputText'
        />
        <label>Endereço Completo</label>
        <textarea type="text"
          placeholder='Digite o endereço completo'
          value={endereco}
          onChange={(e) => setEndereco(e.target.value)}
          className='inputText'
        />
        <label>E-mail Principal para contato</label>
        <input type="tel"
          placeholder='Digite e-mail válido'
          value={emailContato}
          onChange={(e) => setEmailContato(e.target.value)}
          className='inputText'
        />


        <button onClick={cadAluno}>Cadastrar</button>
        <button onClick={pesquisarPosts}>Buscar postagens</button>
        <button onClick={editarPosts}>Editar Posts</button>

        <p>Atualizar posts</p>
        <ul>
          <li>asas</li>
        </ul>

      </div>

    </div>
  );
}

export default App;
