import { db } from './firebaseConect';
import { useState } from 'react';
import { doc, setDoc } from 'firebase/firestore';
import { ToastContainer, toast, addDoc, collection } from 'react-toastify';
import './app.css';


function App() {

  const [titulo, setTitulo] = useState();
  const [autor, setAutor] = useState();


  async function handleAdd() {
    //setDoc, quando passa os paramentros manualmente
   /* await setDoc(doc(db, "posts", "123456"), {
      titulo: titulo,
      autor: autor
    })
      .then(() => {
        console.log('Cadastrado com sucesso');
        setTitulo('');
        setAutor('');
        toast.success('Cadastrado com sucesso!');

      })
      .catch((error) => {
        console.log('gerou erro' + error);

        toast.warn('Error! Algum problema no cadastro.')
      })*/

      addDoc(collection('posts', {
        autor:autor,
        titulo:titulo
      }))
      .then(() => {
        console.log('Cadastrado com sucesso');
        setTitulo('');
        setAutor('');
        toast.success('Cadastrado com sucesso!');

      })
      .catch((error) => {
        console.log('gerou erro' + error);

        toast.warn('Error! Algum problema no cadastro.')
      })
  }




  return (
    <div>
      <h1>reactjs + Firebase ;)</h1>

      <div className='container'>
        <label>Titulo</label>
        <textarea type="text"
          placeholder='Digite seu titulo'
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
        />

        <label>Autor</label>
        <textarea type="text"
          placeholder='Autor do post'
          value={autor}
          onChange={(e) => setAutor(e.target.value)}
        />

        <button onClick={handleAdd}>Cadastrar</button>
      </div>
      <ToastContainer
        position="top-right"
        autoClose={5000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="colored"
      />    </div>
  );
}

export default App;
