import { db } from './firebaseConect';
import { useState } from 'react';
import { addDoc, collection, getDoc } from 'firebase/firestore';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import './app.css';


function App() {

  const [titulo, setTitulo] = useState();
  const [autor, setAutor] = useState();


  async function handleAdd() {
    //setDoc, quando passa os paramentros manualmente
    /* await setDoc(doc(db, "posts", "123456"), {
       titulo: titulo,
       autor: autor
     })
       .then(() => {
         console.log('Cadastrado com sucesso');
         setTitulo('');
         setAutor('');
         toast.success('Cadastrado com sucesso!');

       })
       .catch((error) => {
         console.log('gerou erro' + error);

         toast.warn('Error! Algum problema no cadastro.')
       })*/
    if (titulo === '' || autor === '') {
      toast.warn('Preencha seus dados');
      return;
    } else {
      await addDoc(collection(db, "posts"), {
        titulo: titulo,
        autor: autor
      })
        .then(() => {
          toast.success('Cadastrado com sucesso!');
          setTitulo('');
          setAutor('');
        })
        .catch((error) => {
          alert(error);
        })
    }

  }

async function buscarosts(){
 const meuDB = (db, "posts");
 await getDoc(meuDB)
 .then((s)=> 
 {
  titulo(s.data().titulo)
  autor(s.data().autor)
 })
 .catch((error)=> 
 console.log(error)
 )
}


  return (
    <div>
      <ToastContainer
        position="top-right"
        autoClose={3000}
        hideProgressBar={false}
        newestOnTop={false}
        closeOnClick
        rtl={false}
        pauseOnFocusLoss
        draggable
        pauseOnHover
        theme="light"
      />
      <h1>reactjs + Firebase ;)</h1>

      <div className='container'>
        <label>Titulo</label>
        <textarea type="text"
          placeholder='Digite seu titulo'
          value={titulo}
          onChange={(e) => setTitulo(e.target.value)}
        />

        <label>Autor</label>
        <textarea type="text"
          placeholder='Autor do post'
          value={autor}
          onChange={(e) => setAutor(e.target.value)}
        />

        <button onClick={handleAdd}>Cadastrar</button>
        <button onClick={buscarosts}>Buscar postagens</button>
      </div>
      <div>
        <h1>{titulo.data.titulo}</h1>
        <h1>{autor.data.autor}</h1>
      </div>
    </div>
  );
}

export default App;
